workflow-item.script:
//Get original resource name
var oldName = inputProperties.resourceNames[0];
//Retrieve required properties
var name = inputProperties.customProperties.newname;

//Construct new name
var newName = name;

//Return new name as "resourceNames"
resourceNames = [];
resourceNames[0] = newName;
System.log("Setting machine name from '" + oldName + "' to '" + newName + "'");
